import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Location } from 'src/shared/models/Location';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss']
})
export class ProfileComponent implements OnInit {
  @Input() okmessage?:string;
  @Output() childEndPoint:EventEmitter<Location> = new EventEmitter<Location>;
  currentEndpoint?:string;

  ngOnInit(): void {
    this.currentEndpoint = Location.home;
    console.log(this.currentEndpoint);
  }

  selectPage(){
    this.childEndPoint.emit(Location.profile == this.currentEndpoint ? Location.home:Location.anything);
  }
}
